var closeBy = function( reading1, reading2 ) {
    return distance(
            reading1.latitude,
            reading1.longitude,
            reading2.latitude,
            reading2.longitude ) < 5;
};

var distance = function(lat1, lon1, lat2, lon2) {
  var p = 0.017453292519943295;    // Math.PI / 180
  var c = Math.cos;
  var a = 0.5 - c((lat2 - lat1) * p)/2 + 
          c(lat1 * p) * c(lat2 * p) * 
          (1 - c((lon2 - lon1) * p))/2;

  return 12742000 * Math.asin(Math.sqrt(a)); // 2 * R; R = 6371 km
};

var lastReading = context.get( 'lastReading' ) || {};
var currentReading = msg.payload;

if ( lastReading.latitude && closeBy( currentReading, lastReading ) ) {
