var degreesToDms = function( value, axis ) {

    if ( value < 0 ) { 
        value = value * -1;
        if ( axis == "longitude" ) { direction = "W" } 
        else { direction = "S" };
    } else {  
        if ( axis == "longitude" ) { direction = "E" } 
        else { direction = "N" };
    };

    var degrees = Math.floor( value );
    var minfloat = ( value - degrees ) * 60;
    var minutes = Math.floor( minfloat );
    var secfloat = ( minfloat - minutes ) * 60;
    var seconds = secfloat.toFixed(1);

    if ( seconds == 60 ) { minutes++; seconds = 0; };
    if ( minutes == 60) { degrees++; minutes = 0; };


    return ( "" + 
        degrees + "Â°" + 
        ( "00" + minutes ).slice(-2) + "'" + 
        ( "00" + seconds ).slice(-4) + '"' + 
        direction );
};

